import React, { useState } from 'react';
import './App.css';

function App() {
  // Knowledge base from the PDF
  const knowledgeBase = {
    identity_theft: {
      basics: `Identity theft is the act of wrongfully obtaining someone's personal information without permission. This can include:
      • Name, phone number, address
      • Bank account numbers and details
      • Aadhaar numbers or credit/debit card details
      • PAN card and other identity proofs`,
      effects: `Identity theft can have serious consequences:
      • Gain access to your bank accounts
      • Apply for loans and credit cards in your name
      • File tax refunds fraudulently
      • Obtain driver's license or passport
      • Create fake utility accounts
      • Get medical treatment using your insurance
      • Assume your identity on social media`,
      prevention: `To protect yourself from identity theft:
      • Never share identity proofs with unknown persons/organizations
      • Be careful while using identity proofs at suspicious places
      • Don't share personal details on public platforms
      • Always strike out photocopies with purpose written on them
      • Don't leave credit/debit card receipts in public places
      • Keep monitoring your accounts and statements`
    },
    online_banking: {
      safety: `Tips for safe online banking:
      • Never share your net-banking password, OTP, ATM PIN, or CVV
      • Use strong unique passwords for all accounts
      • Enable 2-factor authentication whenever possible
      • Keep a maximum transaction limit on accounts
      • Register your mobile number for transaction alerts
      • Always check for 'https://' in banking websites`,
      public_wifi: `Be cautious with public WiFi:
      • Avoid logging into banking on public networks
      • Don't save banking passwords in browsers
      • Use virtual keyboard for entering passwords
      • Clear browser history after banking sessions
      • Never do banking on public/shared computers`,
      general: `General banking safety tips:
      • Keep your systems updated with latest security patches
      • Install and maintain antivirus software
      • Monitor your accounts regularly
      • Report suspicious transactions immediately
      • Don't click on links in suspicious emails/messages`
    },
    social_media: {
      safety: `Social media safety tips:
      • Be careful accepting friend requests from strangers
      • Keep your profile private and limit public information
      • Don't share location check-ins in real-time
      • Be cautious about the photos you share
      • Never share personal/financial information
      • Report suspicious or abusive behavior`,
      protection: `Protect your social media accounts:
      • Use strong, unique passwords
      • Enable two-factor authentication
      • Don't use same password across platforms
      • Be careful what apps you give access to
      • Regularly review privacy settings
      • Log out from shared devices`
    },
    incident_reporting: {
      channels: `You can report cyber crime through:
      1. Visit the nearest police station immediately
      2. National Cyber Crime Reporting Portal (cybercrime.gov.in)
      3. Call helpline number 1930
      4. Report phishing at www.reportphishing.in
      5. For stolen phones, call 14422`,
      process: `When reporting a cyber crime:
      • Collect all relevant evidence
      • Note down incident details immediately
      • Keep original copies of all documents
      • Save screenshots of suspicious activities
      • File complaint as soon as possible`
    }
  };

  const [messages, setMessages] = useState([
    { role: 'assistant', content: `Hello! I'm NetSathi, your cyber security awareness assistant. I can help you with:

• Identity theft protection
• Online banking safety
• Social media security
• Reporting cyber crime

What would you like to know about?` }
  ]);

  const [inputText, setInputText] = useState('');

  const getResponse = (input) => {
    const lowercaseInput = input.toLowerCase();

    // Identity Theft Related
    if (lowercaseInput.includes('identity') || lowercaseInput.includes('theft') || lowercaseInput.includes('steal')) {
      if (lowercaseInput.includes('protect') || lowercaseInput.includes('prevent')) {
        return knowledgeBase.identity_theft.prevention;
      } else if (lowercaseInput.includes('effect') || lowercaseInput.includes('happen')) {
        return knowledgeBase.identity_theft.effects;
      }
      return knowledgeBase.identity_theft.basics;
    }

    // Online Banking Related
    if (lowercaseInput.includes('bank') || lowercaseInput.includes('upi') || lowercaseInput.includes('payment')) {
      if (lowercaseInput.includes('wifi') || lowercaseInput.includes('public')) {
        return knowledgeBase.online_banking.public_wifi;
      } else if (lowercaseInput.includes('safe') || lowercaseInput.includes('protect')) {
        return knowledgeBase.online_banking.safety;
      }
      return knowledgeBase.online_banking.general;
    }

    // Social Media Related
    if (lowercaseInput.includes('social') || lowercaseInput.includes('facebook') || 
        lowercaseInput.includes('instagram') || lowercaseInput.includes('profile')) {
      if (lowercaseInput.includes('protect') || lowercaseInput.includes('secure')) {
        return knowledgeBase.social_media.protection;
      }
      return knowledgeBase.social_media.safety;
    }

    // Reporting Related
    if (lowercaseInput.includes('report') || lowercaseInput.includes('complain') || 
        lowercaseInput.includes('crime') || lowercaseInput.includes('police')) {
      if (lowercaseInput.includes('how') || lowercaseInput.includes('process')) {
        return knowledgeBase.incident_reporting.process;
      }
      return knowledgeBase.incident_reporting.channels;
    }

    // Default Response
    return `I can help you with:
    • Identity theft protection
    • Online banking safety
    • Social media security
    • Reporting cyber crime

    Please ask about any of these topics! For example:
    - "What is identity theft?"
    - "How to protect my online banking?"
    - "How to stay safe on social media?"
    - "How do I report cyber crime?"`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Add user message
    const userMessage = { role: 'user', content: inputText };
    setMessages(prev => [...prev, userMessage]);

    // Get and add bot response
    const botResponse = { role: 'assistant', content: getResponse(inputText) };
    setMessages(prev => [...prev, botResponse]);

    // Clear input
    setInputText('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>NetSathi Cyber Security Assistant</h1>
      </header>
      <div className="chat-container">
        <div className="messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              <span className="message-content">{message.content}</span>
            </div>
          ))}
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            placeholder="Type your question here..."
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}

export default App;
