import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Hi! I\'m NetSathi. I can help you learn about cyber security. Try asking me about identity theft, online banking safety, or how to report cyber crime!' }
  ]);
  const [inputText, setInputText] = useState('');

  const getResponse = (input) => {
    const lowercaseInput = input.toLowerCase();

    if (lowercaseInput.includes('identity theft')) {
      return `Identity theft is the act of stealing someone's personal information like:
      • Name and address
      • Bank account details
      • Aadhaar/PAN numbers

      To protect yourself:
      • Never share personal documents with strangers
      • Be careful with ID photocopies
      • Don't share details on public platforms`;
    }

    if (lowercaseInput.includes('banking') || lowercaseInput.includes('bank')) {
      return `Tips for safe online banking:
      • Never share OTP/PIN with anyone
      • Use strong passwords
      • Don't use public WiFi for banking
      • Keep transaction limits on accounts`;
    }

    if (lowercaseInput.includes('report') || lowercaseInput.includes('crime')) {
      return `You can report cyber crime through:
      1. Visit nearest police station
      2. National Cyber Crime Portal (cybercrime.gov.in)
      3. Call helpline 1930
      4. For lost phones, call 14422`;
    }

    return `I can help you with:
    • Identity theft protection
    • Online banking safety
    • How to report cyber crime

    Please ask about any of these topics!`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Add user message
    const userMessage = { role: 'user', content: inputText };
    setMessages(prev => [...prev, userMessage]);

    // Get and add bot response
    const botResponse = { role: 'assistant', content: getResponse(inputText) };
    setMessages(prev => [...prev, botResponse]);

    // Clear input
    setInputText('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>NetSathi Cyber Security Assistant</h1>
      </header>
      <div className="chat-container">
        <div className="messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              <span className="message-content">{message.content}</span>
            </div>
          ))}
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            placeholder="Type your question here..."
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}

export default App;
